import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight, TrendingUp, TrendingDown } from 'lucide-react';

const StockTradingMain = () => {
    const [currentTime, setCurrentTime] = useState(new Date());
    const [currentPage, setCurrentPage] = useState(1);

    // 실시간 시간 업데이트
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    // 샘플 주식 데이터
    const stockData = [
        { rank: 1, name: '한화솔루션', price: 35600, change: -6.1, volume: 27 },
        { rank: 2, name: '담비글로벌', price: 108800, change: 64.1, volume: 24 },
        { rank: 3, name: '프롬바이오', price: 3660, change: 20.7, volume: 17 },
        { rank: 4, name: '시프트업', price: 61300, change: 10.8, volume: 16 },
        { rank: 5, name: '두산에너빌리티', price: 38250, change: -2.1, volume: 9.2 },
        { rank: 6, name: '현대로템', price: 120900, change: 1.0, volume: 7.8 },
        { rank: 7, name: '삼성전자', price: 54800, change: -1.6, volume: 7.7 },
        { rank: 8, name: '지투파워', price: 9220, change: 15.9, volume: 6.9 },
        { rank: 9, name: '펜드론', price: 210000, change: 1.2, volume: 6.4 },
        { rank: 10, name: '현대차', price: 182200, change: -3.0, volume: 6.4 }
    ];

    // 지수 및 환율 데이터
    const indexData = [
        { name: '코스피', value: 2595.45, change: -30.13, percentage: -1.1 },
        { name: '코스닥', value: 718.27, change: -5.35, percentage: -0.7 },
        { name: '나스닥', value: 18872.64, change: -270.07, percentage: -1.4 },
        { name: 'S&P 500', value: 5844.61, change: -95.85, percentage: -1.6 },
        { name: 'VIX', value: 20.87, change: 2.78, percentage: 15.3 },
        { name: '환율', value: 1380.65, change: -6.45, percentage: -0.4 },
        { name: '달러 인덱스', value: 99.57, change: 0.01, percentage: 0.01 }
    ];

    const formatTime = (date) => {
        return date.toLocaleTimeString('ko-KR', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
        });
    };

    const formatPrice = (price) => {
        return price.toLocaleString() + '원';
    };

    const getChangeColor = (change) => {
        if (change > 0) return 'text-red-500 bg-red-900/20';
        if (change < 0) return 'text-blue-400 bg-blue-900/20';
        return 'text-gray-400 bg-gray-900/20';
    };

    const getChangeIcon = (change) => {
        if (change > 0) return <TrendingUp className="w-4 h-4" />;
        if (change < 0) return <TrendingDown className="w-4 h-4" />;
        return null;
    };

    return (
        <div className="min-h-screen bg-gray-900 text-white">
            {/* 헤더 */}
            <div className="bg-blue-600 px-6 py-4">
                <div className="flex justify-between items-center">
                    <div className="flex space-x-8">
                        <span className="text-white">거래소</span>
                        <span className="text-white">내지갑</span>
                    </div>
                    <div className="bg-blue-500 px-4 py-2 rounded">
                        로그인 | 회원가입
                    </div>
                </div>
            </div>

            <div className="flex">
                {/* 메인 콘텐츠 */}
                <div className="flex-1 p-6">
                    {/* 실시간 차트 헤더 */}
                    <div className="mb-6">
                        <div className="flex items-center justify-between mb-4">
                            <h1 className="text-2xl font-bold flex items-center gap-2">
                                실시간 차트
                                <span className="text-sm text-gray-400">오늘 {formatTime(currentTime)} 기준</span>
                            </h1>
                        </div>

                        {/* 필터 탭 */}
