                            <div className="ml-auto">
                                <span className="text-blue-400 text-sm">투자위험 주식 숨기기</span>
                            </div>
                        </div>

                        {/* 시간 필터 */}
                        <div className="flex space-x-4 mb-6">
                            <button className="px-3 py-1 bg-gray-700 rounded text-sm">실시간</button>
                            <button className="px-3 py-1 text-gray-400 text-sm">1일</button>
                            <button className="px-3 py-1 text-gray-400 text-sm">1주일</button>
                            <button className="px-3 py-1 text-gray-400 text-sm">1개월</button>
                            <button className="px-3 py-1 text-gray-400 text-sm">3개월</button>
                            <button className="px-3 py-1 text-gray-400 text-sm">6개월</button>
                            <button className="px-3 py-1 text-gray-400 text-sm">1년</button>
                        </div>
                    </div>

                    {/* 주식 테이블 */}
                    <div className="bg-gray-800 rounded-lg overflow-hidden">
                        <div className="grid grid-cols-5 gap-4 p-4 bg-gray-700 text-sm text-gray-300">
                            <div>종목</div>
                            <div className="text-right">현재가</div>
                            <div className="text-right">등락율</div>
                            <div className="text-right">거래대금 많은 순</div>
                            <div></div>
                        </div>

                        {stockData.map((stock) => (
                            <div key={stock.rank} className="grid grid-cols-5 gap-4 p-4 border-t border-gray-700 hover:bg-gray-750">
                                <div className="flex items-center space-x-3">
                                    <span className="text-gray-400">{stock.rank}</span>
                                    <span className="font-medium">{stock.name}</span>
                                </div>
                                <div className="text-right">{formatPrice(stock.price)}</div>
                                <div className={`text-right px-2 py-1 rounded text-sm ${getChangeColor(stock.change)}`}>
                                    {stock.change > 0 ? '+' : ''}{stock.change}%
