                            <button className="p-1">
                                <ChevronLeft className="w-4 h-4" />
                            </button>
                            {[1, 2, 3, 4, 5].map((page) => (
                                <button
                                    key={page}
                                    className={`w-8 h-8 rounded ${page === currentPage ? 'bg-blue-600' : 'text-gray-400'}`}
                                >
                                    {page}
                                </button>
                            ))}
                            <span className="text-gray-400">...</span>
                            <button className="text-gray-400">10</button>
                            <button className="p-1">
                                <ChevronRight className="w-4 h-4" />
                            </button>
                        </div>
                    </div>
                </div>

                {/* 사이드바 - 지수 및 환율 */}
                <div className="w-80 p-6 bg-gray-800">
                    <h2 className="text-lg font-bold mb-4">주요 지수</h2>
                    <div className="space-y-4">
                        {indexData.map((index, i) => (
                            <div key={i} className="bg-gray-900 p-4 rounded-lg">
                                <div className="flex items-center justify-between mb-2">
                                    <span className="text-gray-300">{index.name}</span>
                                    {getChangeIcon(index.change)}
                                </div>
                                <div className="text-xl font-bold">{index.value.toLocaleString()}</div>
                                <div className={`text-sm ${getChangeColor(index.change)} flex items-center gap-1`}>
