import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight, TrendingUp, TrendingDown } from 'lucide-react';
import './StockTradingMain.css';

const StockTradingMain = () => {
    const [currentTime, setCurrentTime] = useState(new Date());
    const [currentPage, setCurrentPage] = useState(1);

    // 실시간 시간 업데이트
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    // 샘플 주식 데이터
    const stockData = [
        { rank: 1, name: '한화솔루션', price: 35600, change: -6.1, volume: 27 },
        { rank: 2, name: '담비글로벌', price: 108800, change: 64.1, volume: 24 },
        { rank: 3, name: '프롬바이오', price: 3660, change: 20.7, volume: 17 },
        { rank: 4, name: '시프트업', price: 61300, change: 10.8, volume: 16 },
        { rank: 5, name: '두산에너빌리티', price: 38250, change: -2.1, volume: 9.2 },
        { rank: 6, name: '현대로템', price: 120900, change: 1.0, volume: 7.8 },
        { rank: 7, name: '삼성전자', price: 54800, change: -1.6, volume: 7.7 },
        { rank: 8, name: '지투파워', price: 9220, change: 15.9, volume: 6.9 },
        { rank: 9, name: '펜드론', price: 210000, change: 1.2, volume: 6.4 },
        { rank: 10, name: '현대차', price: 182200, change: -3.0, volume: 6.4 }
    ];

    // 지수 및 환율 데이터
    const indexData = [
        { name: '코스피', value: 2595.45, change: -30.13, percentage: -1.1 },
        { name: '코스닥', value: 718.27, change: -5.35, percentage: -0.7 },
        { name: '나스닥', value: 18872.64, change: -270.07, percentage: -1.4 },
        { name: 'S&P 500', value: 5844.61, change: -95.85, percentage: -1.6 },
        { name: 'VIX', value: 20.87, change: 2.78, percentage: 15.3 },
        { name: '환율', value: 1380.65, change: -6.45, percentage: -0.4 },
        { name: '달러 인덱스', value: 99.57, change: 0.01, percentage: 0.01 }
    ];

    const formatTime = (date) => {
        return date.toLocaleTimeString('ko-KR', {
            hour: '2-digit',
            minute: '2-digit',
            hour12: false
        });
    };

    const formatPrice = (price) => {
        return price.toLocaleString() + '원';
    };

    const getChangeClass = (change) => {
        if (change > 0) return 'change-positive';
        if (change < 0) return 'change-negative';
        return 'change-neutral';
    };

    const getChangeIcon = (change) => {
        if (change > 0) return <TrendingUp className="change-icon" />;
        if (change < 0) return <TrendingDown className="change-icon" />;
        return null;
    };

    return (
        <div className="app-container">
            {/* 헤더 */}
            <div className="header">
                <div className="header-content">
                    <div className="header-nav">
                        <span>거래소</span>
                        <span>내지갑</span>
                    </div>
                    <div className="header-auth">
                        로그인 | 회원가입
                    </div>
                </div>
            </div>

            <div className="main-layout">
                {/* 메인 콘텐츠 */}
                <div className="main-content">
                    {/* 실시간 차트 헤더 */}
                    <div className="chart-header">
                        <div className="title-section">
                            <h1 className="main-title">
                                실시간 차트
                                <span className="time-label">오늘 {formatTime(currentTime)} 기준</span>
                            </h1>
                        </div>

                        {/* 필터 탭 */}
                        <div className="filter-tabs">
                            <button className="tab active">토스증권 거래대금</button>
                            <button className="tab">토스증권 거래량</button>
                            <button className="tab">거래대금</button>
                            <button className="tab">거래량</button>
                            <button className="tab">급상승</button>
                            <button className="tab">급하락</button>
                            <button className="tab">인기</button>
                            <div className="tab-right">
                                <span className="risk-filter">투자위험 주식 숨기기</span>
                            </div>
                        </div>

                        {/* 시간 필터 */}
                        <div className="time-filters">
                            <button className="time-tab active">실시간</button>
                            <button className="time-tab">1일</button>
                            <button className="time-tab">1주일</button>
                            <button className="time-tab">1개월</button>
                            <button className="time-tab">3개월</button>
                            <button className="time-tab">6개월</button>
                            <button className="time-tab">1년</button>
                        </div>
                    </div>

                    {/* 주식 테이블 */}
                    <div className="stock-table">
                        <div className="table-header">
                            <div>종목</div>
                            <div>현재가</div>
                            <div>등락율</div>
                            <div>거래대금 많은 순</div>
                            <div></div>
                        </div>

                        {stockData.map((stock) => (
                            <div key={stock.rank} className="table-row">
                                <div className="stock-info">
                                    <span className="rank">{stock.rank}</span>
                                    <span className="name">{stock.name}</span>
                                </div>
                                <div className="price">{formatPrice(stock.price)}</div>
                                <div className={`change ${getChangeClass(stock.change)}`}>
                                    {stock.change > 0 ? '+' : ''}{stock.change}%
                                </div>
                                <div className="volume">{stock.volume}억원</div>
                                <div className="icon-cell">
                                    {getChangeIcon(stock.change)}
                                </div>
                            </div>
                        ))}

                        {/* 페이지네이션 */}
                        <div className="pagination">
                            <button className="page-btn">
                                <ChevronLeft className="page-icon" />
                            </button>
                            {[1, 2, 3, 4, 5].map((page) => (
                                <button
                                    key={page}
                                    className={`page-number ${page === currentPage ? 'active' : ''}`}
                                >
                                    {page}
                                </button>
                            ))}
                            <span className="page-dots">...</span>
                            <button className="page-number">10</button>
                            <button className="page-btn">
                                <ChevronRight className="page-icon" />
                            </button>
                        </div>
                    </div>
                </div>

                {/* 사이드바 - 지수 및 환율 */}
                <div className="sidebar">
                    <h2 className="sidebar-title">주요 지수</h2>
                    <div className="index-list">
                        {indexData.map((index, i) => (
                            <div key={i} className="index-card">
                                <div className="index-header">
                                    <span className="index-name">{index.name}</span>
                                    {getChangeIcon(index.change)}
                                </div>
                                <div className="index-value">{index.value.toLocaleString()}</div>
                                <div className={`index-change ${getChangeClass(index.change)}`}>
                                    {index.change > 0 ? '+' : ''}{index.change.toLocaleString()}
                                    ({index.percentage > 0 ? '+' : ''}{index.percentage}%)
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="news-section">
                        <div className="news-tabs">
                            <button className="news-tab active">주요 뉴스</button>
                            <button className="news-tab">추천 뉴스</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StockTradingMain;
